function semaGoster(){Set.animateSets({container:Interaction.container,position:new Point(130,100),sets:[Interaction.birinciKume.kume,Interaction.ikinciKume.kume],letters:["A","B"]})}var sorgular=function(){this.sorguSecici=Math.floor(Math.random()*4+1),console.log("Soru Seçici: "+this.sorguSecici);switch(this.sorguSecici){case 1:this.kume=new Set({type:Set.SMALLER_THAN,value:6}),this.kume;break;case 2:this.kume=new Set({type:Set.GREATER_THAN_DIGIT_EVEN,value:0}),this.kume;break;case 3:this.kume=new Set({type:Set.DIGIT,value:10}),this.kume;break;case 4:this.kume=new Set({type:Set.SMALLER_THAN_PRIME,value:10}),this.kume}this.uzunluk=function(){return this.kume.elements.length},this.yeniSoru=function(e){return this.soruCesidi=Math.floor(Math.random()*2+1),console.log("Soru Seçici: "+this.soruCesidi),this.isim=e,this.soruCesidi==2?this.kume.getDefinitionString(e):this.kume.getElementsString(e)},this.getKume=function(){return this.kume}};